choose.when:
WHERE b.writer = m.id
	AND b.show = 'Y'
	AND #{map.search_option} LIKE #{map.keyword}

choose.otherwise:
WHERE b.writer = m.id
	AND b.show = 'Y'
	AND(title LIKE #{map.keyword}
		OR search_content LIKE #{map.keyword}
		OR id LIKE #{map.keyword})

mapper.update:
UPDATE tbl_board
	SET viewcnt = viewcnt +1
WHERE bno = #{bno}

UPDATE tbl_board
	SET show = 'n'
WHERE bno = #{bno}

UPDATE tbl_board 
SET re_step = re_step+1
WHERE ref = #{ref} AND re_step > #{re_step}
